import React, { useState, useEffect, useRef } from 'react';
import { courseData } from './data/courseData';
import html2canvas from 'html2canvas';
import _ from 'lodash';

function App() {
  // State for selected subjects and sessions
  const [selectedSubjects, setSelectedSubjects] = useState([]);
  const [selectedSessions, setSelectedSessions] = useState({});
  const [conflicts, setConflicts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [notification, setNotification] = useState(null);
  const fileInputRef = useRef(null);
  const timetableRef = useRef(null);

  // Get available subjects (those not already selected)
  const availableSubjects = courseData.subjects
    .filter(subject => !selectedSubjects.find(s => s.code === subject.code))
    .filter(subject => 
      searchTerm === '' || 
      subject.code.toLowerCase().includes(searchTerm.toLowerCase()) ||
      subject.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

  // Add a subject to the selection
  const addSubject = (subjectCode) => {
    const subject = courseData.subjects.find(s => s.code === subjectCode);
    if (subject) {
      setSelectedSubjects([...selectedSubjects, subject]);
    }
  };

  // Remove a subject from the selection
  const removeSubject = (subjectCode) => {
    setSelectedSubjects(selectedSubjects.filter(s => s.code !== subjectCode));

    // Clear selected sessions for this subject
    const newSelectedSessions = {...selectedSessions};
    Object.keys(newSelectedSessions).forEach(key => {
      if (key.startsWith(subjectCode)) {
        delete newSelectedSessions[key];
      }
    });
    setSelectedSessions(newSelectedSessions);
  };

  // Select a session option for a component
  const selectSession = (subjectCode, componentName, optionId) => {
    const newSelectedSessions = {
      ...selectedSessions,
      [`${subjectCode}-${componentName}`]: optionId
    };
    setSelectedSessions(newSelectedSessions);

    // Check for conflicts
    detectConflicts(newSelectedSessions);
  };

  // Check for time conflicts between selected sessions
  const detectConflicts = (selections) => {
    const sessions = [];

    // Get all sessions from selections
    Object.entries(selections).forEach(([key, optionId]) => {
      const [subjectCode, componentName] = key.split('-');
      const subject = selectedSubjects.find(s => s.code === subjectCode);
      if (!subject) return;

      const component = subject.components.find(c => c.name === componentName);
      if (!component) return;

      const option = component.options.find(o => o.id === optionId);
      if (!option) return;

      option.sessions.forEach(session => {
        sessions.push({
          id: `${optionId}-${session.day}-${session.startTime}`,
          subjectCode,
          day: session.day,
          startTime: session.startTime,
          endTime: session.endTime,
          ...session
        });
      });
    });

    // Detect conflicts
    const newConflicts = [];
    for (let i = 0; i < sessions.length; i++) {
      for (let j = i + 1; j < sessions.length; j++) {
        const sessionA = sessions[i];
        const sessionB = sessions[j];

        if (sessionA.day === sessionB.day) {
          // Check for time overlap
          const startA = timeToMinutes(sessionA.startTime);
          const endA = timeToMinutes(sessionA.endTime);
          const startB = timeToMinutes(sessionB.startTime);
          const endB = timeToMinutes(sessionB.endTime);

          if ((startA <= startB && endA > startB) || 
              (startB <= startA && endB > startA)) {
            newConflicts.push([sessionA.id, sessionB.id]);
          }
        }
      }
    }

    setConflicts(newConflicts);
  };

  // Helper function to convert time to minutes for comparison
  const timeToMinutes = (timeStr) => {
    const [hours, minutes] = timeStr.split(':').map(Number);
    return hours * 60 + minutes;
  };

  // Get all sessions to display in the timetable
  const getAllSessions = () => {
    const allSessions = [];

    selectedSubjects.forEach(subject => {
      subject.components.forEach(component => {
        component.options.forEach(option => {
          const isSelected = selectedSessions[`${subject.code}-${component.name}`] === option.id;

          option.sessions.forEach(session => {
            allSessions.push({
              id: `${option.id}-${session.day}-${session.startTime}`,
              subjectCode: subject.code,
              componentName: component.name,
              optionId: option.id,
              selected: isSelected,
              day: session.day,
              startTime: session.startTime,
              endTime: session.endTime,
              location: session.location,
              instructor: session.instructor,
              type: session.type
            });
          });
        });
      });
    });

    // Group sessions by day for detecting all types of overlaps
    const sessionsByDay = {};
    allSessions.forEach(session => {
      if (!sessionsByDay[session.day]) {
        sessionsByDay[session.day] = [];
      }
      sessionsByDay[session.day].push(session);
    });

    // Process each day to find overlapping sessions
    Object.entries(sessionsByDay).forEach(([day, daySessions]) => {
      // Sort sessions by start time
      daySessions.sort((a, b) => timeToMinutes(a.startTime) - timeToMinutes(b.startTime));

      // For each session, find all other sessions that overlap with it
      const overlapGroups = [];

      for (let i = 0; i < daySessions.length; i++) {
        const session = daySessions[i];
        const sessionStart = timeToMinutes(session.startTime);
        const sessionEnd = timeToMinutes(session.endTime);

        // Find existing overlap group or create new one
        let foundGroup = false;

        for (const group of overlapGroups) {
          // Check if this session overlaps with any session in the group
          const overlapsWithGroup = group.sessions.some(groupSession => {
            const groupSessionStart = timeToMinutes(groupSession.startTime);
            const groupSessionEnd = timeToMinutes(groupSession.endTime);

            // Check for overlap
            return (sessionStart < groupSessionEnd && sessionEnd > groupSessionStart);
          });

          if (overlapsWithGroup) {
            group.sessions.push(session);
            foundGroup = true;
            break;
          }
        }

        // If no overlap group found, create a new one
        if (!foundGroup) {
          overlapGroups.push({
            sessions: [session]
          });
        }
      }

      // Merge overlap groups that share sessions
      let mergedGroups = [...overlapGroups];
      let didMerge = true;

      while (didMerge) {
        didMerge = false;

        for (let i = 0; i < mergedGroups.length; i++) {
          for (let j = i + 1; j < mergedGroups.length; j++) {
            const group1 = mergedGroups[i];
            const group2 = mergedGroups[j];

            // Check if groups share any sessions
            const sharedSessions = group1.sessions.some(session1 => 
              group2.sessions.includes(session1)
            );

            if (sharedSessions) {
              // Merge the groups
              group1.sessions = [...new Set([...group1.sessions, ...group2.sessions])];
              mergedGroups.splice(j, 1);
              didMerge = true;
              break;
            }
          }
          if (didMerge) break;
        }
      }

      // Add overlap information to each session in the groups
      mergedGroups.forEach(group => {
        if (group.sessions.length > 1) {
          group.sessions.forEach((session, index) => {
            session.hasOverlap = true;
            session.overlapIndex = index;
            session.overlapCount = group.sessions.length;
          });
        }
      });
    });

    return allSessions;
  };

  // Ensure timetable measurements are updated when window resizes
  useEffect(() => {
    const handleResize = () => {
      // Force a re-render to recalculate positions
      setSelectedSubjects([...selectedSubjects]);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [selectedSubjects]);

  // Check if a session has a conflict
  const hasConflict = (sessionId) => {
    return conflicts.some(conflict => 
      conflict[0] === sessionId || conflict[1] === sessionId
    );
  };

  // Export timetable as JSON
  const exportJson = () => {
    if (selectedSubjects.length === 0) {
      showNotification('Please select at least one subject before exporting', 'warning');
      return;
    }

    const data = {
      selectedSubjects: selectedSubjects.map(s => s.code),
      selectedSessions
    };

    const dataStr = JSON.stringify(data, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);

    const exportFileDefaultName = 'timetable.json';

    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();

    showNotification('Timetable exported successfully!', 'success');
  };

  // Import timetable from JSON
  const importJson = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setIsLoading(true);

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const data = JSON.parse(e.target.result);

        // Reset current selections
        setSelectedSubjects([]);
        setSelectedSessions({});

        // Add subjects
        const subjectPromises = data.selectedSubjects.map(code => {
          const subject = courseData.subjects.find(s => s.code === code);
          return subject;
        }).filter(Boolean);

        setSelectedSubjects(subjectPromises);

        // Set selected sessions
        setSelectedSessions(data.selectedSessions);

        // Check for conflicts
        setTimeout(() => {
          detectConflicts(data.selectedSessions);
          setIsLoading(false);
          showNotification('Timetable imported successfully!', 'success');
        }, 100);
      } catch (error) {
        console.error('Error importing timetable:', error);
        setIsLoading(false);
        showNotification('Error importing timetable. Please check the file format.', 'error');
      }
    };

    reader.readAsText(file);

    // Reset file input
    event.target.value = '';
  };

  // Export timetable as image
  const exportImage = () => {
    if (!timetableRef.current) return;

    html2canvas(timetableRef.current).then(canvas => {
      const image = canvas.toDataURL('image/png');
      const link = document.createElement('a');
      link.href = image;
      link.download = 'timetable.png';
      link.click();
    });
  };

  // Show notification
  const showNotification = (message, type = 'info') => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 3000);
  };

  // Get calendar time slots
  const timeSlots = [];
  for (let hour = 8; hour < 21; hour++) {
    for (let minute of [0, 30]) {
      if (hour === 8 && minute === 0) continue; // Start at 8:30
      if (hour === 20 && minute === 30) continue; // End at 20:30

      const time = `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;
      timeSlots.push(time);
    }
  }

  // Days of the week
  const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'];

  // Calculate session position and height in the timetable grid
  const getSessionStyle = (session) => {
    const startMinutes = timeToMinutes(session.startTime);
    const endMinutes = timeToMinutes(session.endTime);
    const duration = endMinutes - startMinutes;

    // 8:30 starts at 0
    const startFromFirstSlot = startMinutes - timeToMinutes('08:30');

    // Each 30 minutes is 60px high
    const top = (startFromFirstSlot / 30) * 60;
    const height = (duration / 30) * 60;

    return {
      top: `${top}px`,
      height: `${height}px`
    };
  };

  // Determine session card color based on selection status
  const getSessionColor = (session) => {
    if (hasConflict(session.id) && session.selected) {
      return 'bg-red-300 border-red-500'; // Conflict
    }
    if (session.selected) {
      return 'bg-green-300 border-green-500'; // Selected
    }
    return 'bg-gray-200 border-gray-400'; // Available but not selected
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      {/* Loading Overlay */}
      {isLoading && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-4 rounded shadow">
            <p className="text-lg">Loading...</p>
          </div>
        </div>
      )}

      {/* Notification */}
      {notification && (
        <div className={`fixed top-4 right-4 p-3 rounded shadow-lg z-50 ${
          notification.type === 'success' ? 'bg-green-500 text-white' :
          notification.type === 'error' ? 'bg-red-500 text-white' :
          notification.type === 'warning' ? 'bg-yellow-500 text-white' :
          'bg-blue-500 text-white'
        }`}>
          {notification.message}
        </div>
      )}

      {/* Header */}
      <header className="bg-blue-700 text-white p-4">
        <div className="container mx-auto flex flex-col sm:flex-row justify-between items-center">
          <h1 className="text-2xl font-bold mb-2 sm:mb-0">University Timetable Scheduler</h1>
          <div className="space-x-2 flex flex-wrap justify-center">
            <button 
              onClick={exportJson}
              className="bg-white text-blue-700 px-3 py-1 rounded mb-2 sm:mb-0"
              disabled={isLoading}
            >
              Export JSON
            </button>
            <button 
              onClick={exportImage}
              className="bg-white text-blue-700 px-3 py-1 rounded"
              disabled={isLoading}
            >
              Export Image
            </button>
            <input
              type="file"
              accept=".json"
              className="hidden"
              ref={fileInputRef}
              onChange={importJson}
            />
            <button 
              onClick={() => fileInputRef.current.click()}
              className="bg-white text-blue-700 px-3 py-1 rounded"
              disabled={isLoading}
            >
              Import JSON
            </button>
          </div>
        </div>
      </header>

      <div className="container mx-auto p-4 flex flex-col lg:flex-row gap-4">
        {/* Subject Selection Panel */}
        <div className="w-full lg:w-1/3 bg-white p-4 rounded shadow">
          <h2 className="text-xl font-bold mb-4">Subject Selection</h2>

          {/* Search and Add Subject */}
          <div className="mb-4">
            <label className="block mb-2">Search Subjects:</label>
            <div className="flex mb-2">
              <input 
                type="text" 
                className="flex-grow p-2 border rounded-l"
                placeholder="Search by code or name"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <button 
                className="bg-gray-200 px-3 rounded-r border-t border-r border-b"
                onClick={() => setSearchTerm('')}
              >
                Clear
              </button>
            </div>

            <div className="max-h-60 overflow-y-auto border rounded">
              {availableSubjects.length === 0 ? (
                <p className="p-2 text-gray-500">No subjects found</p>
              ) : (
                availableSubjects.map(subject => (
                  <div 
                    key={subject.code} 
                    className="p-2 hover:bg-blue-50 cursor-pointer border-b flex justify-between items-center"
                    onClick={() => addSubject(subject.code)}
                  >
                    <div>
                      <div className="font-bold">{subject.code}</div>
                      <div className="text-sm">{subject.name}</div>
                    </div>
                    <button className="text-blue-600 text-sm">Add</button>
                  </div>
                ))
              )}
            </div>
          </div>

          {/* Selected Subjects */}
          <div>
            <h3 className="font-bold mb-2">Selected Subjects:</h3>
            {selectedSubjects.length === 0 ? (
              <p className="text-gray-500">No subjects selected</p>
            ) : (
              <div className="space-y-4">
                {selectedSubjects.map(subject => (
                  <div key={subject.code} className="border p-3 rounded bg-gray-50 hover:bg-gray-100 transition-colors">
                    <div className="flex justify-between items-center mb-2">
                      <h4 className="font-bold">{subject.code} - {subject.name}</h4>
                      <button 
                        onClick={() => removeSubject(subject.code)}
                        className="text-red-500 hover:text-red-700 p-1 rounded hover:bg-red-50"
                      >
                        Remove
                      </button>
                    </div>

                    {/* Component Selectors */}
                    {subject.components.map(component => (
                      <div key={`${subject.code}-${component.name}`} className="mb-3">
                        <h5 className="font-semibold border-b pb-1 mb-2">{component.name}:</h5>
                        <div className="pl-4">
                          {component.options.map(option => {
                            const firstSession = option.sessions[0];
                            const isSelected = selectedSessions[`${subject.code}-${component.name}`] === option.id;

                            // Check if this option has any conflicts
                            const hasSessionConflict = option.sessions.some(session => {
                              const sessionId = `${option.id}-${session.day}-${session.startTime}`;
                              return hasConflict(sessionId);
                            });

                            return (
                              <div 
                                key={option.id} 
                                className={`flex items-center space-x-2 my-1 p-1 rounded ${
                                  isSelected ? 'bg-green-100' : 
                                  hasSessionConflict && isSelected ? 'bg-red-100' : ''
                                }`}
                              >
                                <input 
                                  type="radio"
                                  id={option.id}
                                  name={`${subject.code}-${component.name}`}
                                  checked={isSelected}
                                  onChange={() => selectSession(subject.code, component.name, option.id)}
                                />
                                <label htmlFor={option.id} className="text-sm flex flex-col">
                                  <span>{firstSession.day} {firstSession.startTime}-{firstSession.endTime}</span>
                                  <span className="text-xs text-gray-600">{firstSession.location}</span>
                                  <span className="text-xs text-gray-600">{firstSession.instructor}</span>
                                </label>
                                {option.sessions.length > 1 && (
                                  <span className="text-xs bg-yellow-100 px-1 rounded">+{option.sessions.length - 1} more</span>
                                )}
                                {hasSessionConflict && isSelected && (
                                  <span className="text-xs text-red-600 font-bold">Conflict!</span>
                                )}
                              </div>
                            );
                          })}
                        </div>
                      </div>
                    ))}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Timetable Grid */}
        <div className="w-full lg:w-2/3 bg-white p-4 rounded shadow overflow-x-auto">
          <h2 className="text-xl font-bold mb-4">Weekly Timetable</h2>

          <div ref={timetableRef} className="relative" style={{ minWidth: "800px" }}>
            {/* Day Headers */}
            <div className="flex border-b">
              <div className="w-20 font-bold p-2 border-r">Time</div>
              <div className="flex-1 flex">
                {days.map(day => (
                  <div key={day} className="flex-1 font-bold p-2 text-center border-r">
                    {day}
                  </div>
                ))}
              </div>
            </div>

            {/* Time Slots */}
            <div className="relative">
              {timeSlots.map((time, index) => (
                <div key={time} className="flex border-b" style={{ height: "60px" }}>
                  <div className="w-20 p-2 text-xs border-r">{time}</div>
                  <div className="flex-1 flex">
                    {days.map(day => (
                      <div 
                        key={`${day}-${time}`} 
                        className="flex-1 border-r relative"
                        style={{ height: "100%" }}
                      ></div>
                    ))}
                  </div>
                </div>
              ))}

              {/* Session Cards */}
              {getAllSessions().map(session => {
                const dayIndex = days.indexOf(session.day);
                if (dayIndex === -1) return null;

                const style = getSessionStyle(session);
                const colorClass = getSessionColor(session);

                // Calculate position for the current day column
                // First get the width of the time column (w-20 = 5rem = 80px)
                const timeColumnWidth = 80;

                // Get the timetable container width
                const timetableContainer = timetableRef.current;
                const containerWidth = timetableContainer?.clientWidth || 800;

                // Calculate the total available width for day columns
                const totalWidth = containerWidth - timeColumnWidth;
                const dayColumnWidth = totalWidth / days.length;

                // Calculate position adjustments for overlapping sessions
                let overlapWidth = 0;
                let overlapOffset = 0;

                if (session.hasOverlap) {
                  // If sessions overlap, divide the column width evenly among them
                  overlapWidth = dayColumnWidth / session.overlapCount;
                  overlapOffset = overlapWidth * session.overlapIndex;
                } else {
                  // If no overlap, use the full column width
                  overlapWidth = dayColumnWidth;
                  overlapOffset = 0;
                }

                // Left position is time column width + (day index * day column width) + overlap offset
                const leftPosition = timeColumnWidth + (dayIndex * dayColumnWidth) + overlapOffset;

                // Add a minimum width for very narrow cards
                const minWidth = 80; // Minimum width in pixels
                overlapWidth = Math.max(overlapWidth, minWidth / session.overlapCount);

                return (
                  <div 
                    key={session.id}
                    className={`absolute border-2 p-1 overflow-hidden text-xs rounded shadow-sm hover:shadow-md transition-shadow ${colorClass} ${session.selected ? 'cursor-default' : 'cursor-pointer opacity-70 hover:opacity-100'}`}
                    style={{
                      ...style,
                      left: `${leftPosition}px`,
                      width: `${overlapWidth - 6}px`,
                      marginLeft: '3px',
                      marginRight: '3px',
                      boxSizing: 'border-box'
                    }}
                    onClick={() => {
                      if (!session.selected) {
                        selectSession(session.subjectCode, session.componentName, session.optionId);
                      }
                    }}
                  >
                    <div className="font-bold">{session.subjectCode}</div>
                    <div className="font-semibold">{session.type}</div>
                    <div className="text-xs">{session.startTime}-{session.endTime}</div>
                    <div className="text-xs truncate">{session.location}</div>
                    <div className="text-xs truncate italic">{session.instructor}</div>
                    {hasConflict(session.id) && session.selected && (
                      <div className="absolute top-0 right-0 w-4 h-4 flex items-center justify-center bg-red-500 text-white rounded-full text-xs font-bold">!</div>
                    )}
                  </div>
                );
              })}
            </div>
          </div>

          {/* Conflict Warning */}
          {conflicts.length > 0 && (
            <div className="mt-4 p-2 bg-red-100 text-red-700 rounded">
              Warning: Your schedule has {conflicts.length} time conflicts. Please resolve them.
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
